import { createContext, useState } from "react";

const ThemeContext = createContext();

export function ThemeProvider(props) {
  const [theme, setTheme] = useState();

  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {props.children}
    </ThemeContext.Provider>
  );
}
