import { useState } from "react";

export default function Signup() {
  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    address: "",
    city: "",
    postcode: "",
    vat: "",
    phone: "",
    email: "",
    password: "",
    passwordConfirm: "",
  });

  const handleUserState = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value });
  };



  return (
    <div className="layout">
      <h1>Signup</h1>
      <form >
        <label>First Name</label>
        <input name="firstName" type="text" value={user.firstName} placeholder="First Name" onChange={handleUserState} />
        <label>Last Name</label>
        <input name="lastName" type="text" value={user.lastName} placeholder="Last Name" onChange={handleUserState} />
        <label>Address</label>
        <input name="address" type="text" value={user.address} placeholder="Address" onChange={handleUserState} />
        <label>City</label>
        <input name="city" type="text" value={user.city} placeholder="" onChange={handleUserState} />
        <label>ZIP Code</label>
        <input name="postcode" type="" value={user.postcode} placeholder="First Name" onChange={handleUserState} />
        <label>VAT Number</label>
        <input name="vat" type="number" value={user.tax} placeholder="First Name" onChange={handleUserState} />
        <label>Phone Number</label>
        <input name="phone" type="tel" value={user.phone} placeholder="First Name" onChange={handleUserState} />
        <label>E-mail</label>
        <input name="email" type="email" value={user.email} placeholder="First Name" onChange={handleUserState} />
        <label>Password</label>
        <input name="password" type="password" value={user.password} placeholder="First Name" onChange={handleUserState} />
        <label>Confirm Password</label>
        <input
          name="passwordConfirm"
          type="password"
          value={user.passwordConfirm} 
          onChange={handleUserState}
        />
      </form>
    </div>
  );
}
